import React, { useEffect, useState } from 'react';
import axios from 'axios';
import APIBASE from '../config';

const cardStyle = {
  background: 'linear-gradient(135deg, #b39ddb 0%, #ce93d8 100%)',
  color: '#222',
  borderRadius: '1rem',
  padding: '2rem',
  margin: '1rem',
  flex: 1,
  boxShadow: '0 2px 16px rgba(79, 38, 131, 0.12)'
};

const dashboardStyle = {
  display: 'flex',
  gap: '1rem'
};

export default function Dashboard() {
  const [counts, setCounts] = useState({ products: 0, orders: 0, shipments: 0 });

  useEffect(() => {
    async function fetchCounts() {
      try {
        const [products, orders] = await Promise.all([
          axios.get(`${APIBASE}/products`),
          axios.get(`${APIBASE}/orders`)
        ]);
        setCounts({
          products: products.data.length,
          orders: orders.data.length,
          shipments: orders.data.filter(o => o.status === 'shipped').length
        });
      } catch (error) {
        console.error(error);
      }
    }
    fetchCounts();
  }, []);

  return (
    <div>
      <h2>Dashboard Overview</h2>
      <div style={dashboardStyle}>
        <div style={cardStyle}>
          <h3>Products</h3>
          <p>{counts.products}</p>
        </div>
        <div style={cardStyle}>
          <h3>Orders</h3>
          <p>{counts.orders}</p>
        </div>
        <div style={cardStyle}>
          <h3>Shipments</h3>
          <p>{counts.shipments}</p>
        </div>
      </div>
    </div>
  );
}
