import React, { useState, useEffect } from 'react';
import axios from 'axios';
import APIBASE from '../config';

const cardStyle = {
  background: 'linear-gradient(135deg, #e1bee7, #b39ddb)',
  color: '#222',
  borderRadius: '16px',
  padding: '2rem',
  margin: '1rem',
  boxShadow: '0 2px 12px rgba(80,36,122,0.07)'
};

export default function Dashboard() {
  const [counts, setCounts] = useState({ products: 0, orders: 0, shipped: 0 });
  useEffect(() => {
    const fetchData = async () => {
      const [prod, ord] = await Promise.all([
        axios.get(`${APIBASE}/products`),
        axios.get(`${APIBASE}/orders`)
      ]);
      setCounts({
        products: prod.data.length,
        orders: ord.data.length,
        shipped: ord.data.filter(x => x.status === 'shipped').length
      });
    };
    fetchData();
  }, []);
  return (
    <div style={{ padding: '2rem', background: '#ede7f6', borderRadius: '1rem' }}>
      <h2>Dashboard</h2>
      <div style={{ display: 'flex', gap: '1.5rem' }}>
        <div style={cardStyle}><h3>Products</h3><p>{counts.products}</p></div>
        <div style={cardStyle}><h3>Orders</h3><p>{counts.orders}</p></div>
        <div style={cardStyle}><h3>Shipped</h3><p>{counts.shipped}</p></div>
      </div>
    </div>
  );
}
