import React, { useEffect, useState } from "react";
import axios from "axios";
import API_BASE from "../config";

const statCardStyle = {
  background: "#f9f7fc",
  borderRadius: "1rem",
  padding: "1.5rem",
  margin: "1rem",
  textAlign: "center",
  boxShadow: "0 2px 14px rgba(137, 87, 255, 0.08)"
};
const sidePanelStyle = {
  background: "#e1bee7",
  borderRadius: "1rem",
  padding: "1.5rem",
  marginLeft: "2rem",
  minWidth: "240px"
};

export default function Dashboard() {
  const [stats, setStats] = useState({
    products: 0,
    orders: 0,
    shipped: 0,
    revenue: 0
  });
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    function fetchStats() {
      Promise.all([
        axios.get(`${API_BASE}/products`),
        axios.get(`${API_BASE}/orders`)
      ]).then(([prodRes, orderRes]) => {
        const products = prodRes.data;
        const orders = orderRes.data;
        setStats({
          products: products.length,
          orders: orders.length,
          shipped: orders.filter(o => o.status === "shipped").length,
          revenue: orders.reduce((tot, o) => tot + (o.totalprice || 0), 0) / 100
        });
        setNotifications([
          ...orders.slice(-5).reverse().map(o => ({
            text: `Order ${o.id} ${o.status === "shipped" ? "dispatched" : "placed"}`,
            time: o.created_at || "",
            type: o.status === "shipped" ? "success" : "default"
          }))
        ]);
      });
    }
    fetchStats();
    const interval = setInterval(fetchStats, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      display: "flex", padding: "2rem", background: "#f6f0fa", minHeight: "80vh"
    }}>
      <div style={{ flex: 2 }}>
        <h2 style={{ color: "#6a52b3" }}>Dashboard Overview</h2>
        <div style={{ display: "flex", gap: "2rem", marginBottom: "2.5rem" }}>
          <div style={statCardStyle}>
            <div style={{ fontSize: "2.2rem" }}>{stats.products}</div>
            <div>Total Products</div>
          </div>
          <div style={statCardStyle}>
            <div style={{ fontSize: "2.2rem" }}>{stats.orders}</div>
            <div>Total Orders</div>
          </div>
          <div style={statCardStyle}>
            <div style={{ fontSize: "2.2rem" }}>{stats.shipped}</div>
            <div>Shipments</div>
          </div>
          <div style={statCardStyle}>
            <div style={{ fontSize: "2.2rem" }}>₹{stats.revenue}</div>
            <div>Total Revenue</div>
          </div>
        </div>
        <div>
          <h3>Activity Feed</h3>
          <div style={{
            background: "#e9dbf4",
            borderRadius: "0.7rem",
            padding: "1rem",
            marginTop: "1rem"
          }}>
            {notifications.length === 0 ? <>
              No recent activity
            </> : notifications.map((n, i) => (
              <div key={i} style={{
                marginBottom: "0.9rem",
                fontSize: "1.05rem",
                display: "flex",
                alignItems: "center"
              }}>
                <span style={{
                  marginRight: 8,
                  color: n.type === "success" ? "#35da8d" : "#6a52b3"
                }}>●</span>
                <span>{n.text}</span>
                <span style={{
                  fontSize: "0.92rem",
                  color: "#7c43bd",
                  marginLeft: "auto"
                }}>{n.time}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div style={sidePanelStyle}>
        <h3 style={{ color: "#6a52b3" }}>Notifications</h3>
        <div>
          {notifications.length === 0 ? <div>No notifications</div> : notifications.map((n, i) => (
            <div key={i} style={{ marginBottom: "1rem" }}>
              <span style={{
                color: n.type === "success" ? "#35da8d" : "#f2a300",
                fontWeight: "bold"
              }}>●</span> {n.text}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
