import React, { useEffect, useState } from "react";
import axios from "axios";
import API_BASE from "../config";

const statsCard = {
  background: "#fcfafc",
  borderRadius: "1rem",
  padding: "1.5rem",
  boxShadow: "0 4px 14px rgba(100,100,180,0.09)",
  margin: "1rem 0",
  minWidth: "190px",
  textAlign: "center",
  fontSize: "1.4rem",
  color: "#38306b",
  fontWeight: "bold",
};

const notificationCard = {
  background: "#ebebf5",
  borderRadius: "1rem",
  padding: "1rem",
  boxShadow: "0 2px 10px rgba(159,122,220,0.12)",
  marginTop: "1.5rem",
};

export default function Dashboard() {
  const [stats, setStats] = useState({
    totalProducts: 0,
    totalOrders: 0,
    shippedOrders: 0,
    revenue: 0,
  });
  const [notifications, setNotifications] = useState([]);

  // Poll every 5 seconds for live updates
  useEffect(() => {
    function fetchDashboard() {
      Promise.all([
        axios.get(`${API_BASE}/products`),
        axios.get(`${API_BASE}/orders`)
      ]).then(([prodRes, orderRes]) => {
        const products = prodRes.data;
        const orders = orderRes.data;
        setStats({
          totalProducts: products.length,
          totalOrders: orders.length,
          shippedOrders: orders.filter(o => o.status === "shipped").length,
          revenue:
            orders.reduce((total, o) => total + (o.totalprice || 0), 0) / 100, // assuming cents
        });
        setNotifications([
          ...orders.slice(-5).reverse().map(o => ({
            text: `Order ${o.id} ${o.status}`,
            time: o.created_at || "",
            type: o.status === "shipped" ? "success" : "info",
          })),
          // You could also add notifications for product added/low stock logic here by checking products
        ]);
      });
    }
    fetchDashboard();
    const interval = setInterval(fetchDashboard, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{
      display: "grid", gridTemplateColumns: "3fr 1fr",
      gap: "3rem", padding: "2.5rem", background: "#f6f0fa", minHeight: "70vh",
      borderRadius: "1.5rem"
    }}>
      <div>
        <h2 style={{ fontSize: "2rem", color: "#6a52b3" }}>Dashboard Overview</h2>
        <div style={{ display: "flex", gap: "2rem", marginBottom: "2rem" }}>
          <div style={statsCard}>
            <div style={{fontSize: "2.2rem"}}>{stats.totalProducts}</div>
            <div>Total Products</div>
          </div>
          <div style={statsCard}>
            <div style={{fontSize: "2.2rem"}}>{stats.totalOrders}</div>
            <div>Total Orders</div>
          </div>
          <div style={statsCard}>
            <div style={{fontSize: "2.2rem"}}>{stats.shippedOrders}</div>
            <div>Shipments Completed</div>
          </div>
          <div style={statsCard}>
            <div style={{fontSize: "2.2rem"}}>₹{stats.revenue.toLocaleString()}</div>
            <div>Total Revenue</div>
          </div>
        </div>
        <div>
          <h3>Recent Activity</h3>
          <div style={{ background: "#e4d5ee", borderRadius: "1rem", padding: "1rem" }}>
            {notifications.length === 0 ? (
              <div>No recent activity</div>
            ) : (
              notifications.map((note, idx) => (
                <div key={idx} style={{
                  display: "flex", alignItems: "center", gap: "1rem", marginBottom: "0.7rem"
                }}>
                  <span style={{
                    fontSize: "1.2rem",
                    color: note.type === "success" ? "#38c381" : "#9575cd"
                  }}>●</span>
                  <span>{note.text}</span>
                  <span style={{fontSize: "0.95rem", color: "#7c43bd", marginLeft: "auto"}}>
                    {note.time}
                  </span>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
      <div>
        <h3 style={{ color: "#6a52b3" }}>Notifications</h3>
        <div style={notificationCard}>
          {notifications.length === 0 ? (
            <div>No notifications.</div>
          ) : (
            notifications.map((note, idx) => (
              <div key={idx} style={{marginBottom: "1rem"}}>
                <span style={{
                  marginRight: 8,
                  color: note.type === "success" ? "#38c381" : "#f2a300",
                  fontWeight: "bold"
                }}>●</span>
                {note.text}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
