import React, { useEffect, useState } from "react";
import axios from "axios";
import API_BASE from "../config"; // Make sure ../config.js exists and exports the API base URL

const pageStyle = {
  background: "#f3e5f5",
  borderRadius: "1rem",
  padding: "2rem"
};
const cardStyle = {
  background: "#e1bee7",
  padding: "1rem",
  borderRadius: "0.5rem",
  marginBottom: "2rem",
  boxShadow: "0 2px 8px rgba(119, 77, 166, 0.08)"
};

export default function Products() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [stock, setStock] = useState("");

  useEffect(() => { fetchProducts(); }, []);

  function fetchProducts() {
    axios.get(`${API_BASE}/products/`)
      .then(r => setProducts(r.data))
      .catch(console.error);
  }
  function addProduct() {
    axios.post(`${API_BASE}/products/`, { name, price: parseInt(price), stock: parseInt(stock) })
      .then(() => {
        setName(""); setPrice(""); setStock(""); fetchProducts();
      })
      .catch(e => alert(e.response?.data?.detail || e.message));
  }
  function restockProduct(productId, newStock) {
  axios.put(`${API_BASE}/products/${productId}/restock?stock=${newStock}`)
    .then(() => fetchProducts())
    .catch(e => alert(e.response?.data?.detail || e.message));
}



  return (
    <div style={pageStyle}>
      <h2>Products</h2>
      <div style={cardStyle}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
        <input placeholder="Price (cents)" value={price} onChange={e => setPrice(e.target.value)} style={{ marginLeft: 8 }} />
        <input placeholder="Stock" value={stock} onChange={e => setStock(e.target.value)} style={{ marginLeft: 8 }} />
        <button onClick={addProduct} style={{ marginLeft: 8 }}>Add</button>
      </div>
      <table style={{ width: "100%", background: "#ede7f6", borderRadius: "0.5rem" }}>
        <thead>
          <tr style={{ background: "#9575cd" }}>
            <th>ID</th>
            <th>Name</th>
            <th>Price (cents)</th>
            <th>Stock</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.name}</td>
              <td>{p.price}</td>
              <td>{p.stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
{products.map((p) => (
  <tr key={p.id}>
    <td>{p.id}</td>
    <td>{p.name}</td>
    <td>{p.price}</td>
    <td>{p.stock}</td>
    <td>
      <input
        type="number"
        min="1"
        placeholder="New Stock"
        value={p.newStock || ""}
        onChange={e =>
          setProducts(products.map(prod =>
            prod.id === p.id ? { ...prod, newStock: e.target.value } : prod
          ))
        }
        style={{ width: "60px" }}
      />
      <button
        onClick={() => restockProduct(p.id, p.newStock)}
        disabled={!p.newStock}
      >
        Restock
      </button>
    </td>
  </tr>
))}
